INSERT INTO `vocabulary` VALUES (1,'recipe','/ˈresəpi/','audio/8bc5ec564d3e460f876f71876b900e17.mp3','Late Middle English: from Old French recette, from Old French recetter ‘to receive’, from Latin recipere.',1),(2,'ingredient','/ɪnˈɡriːdiənt/','audio/bf5621a91fe64396b4cd053b5615012f.mp3','Late Middle English: from Old French ingredient, from Latin ingrediens, present participle of ingredi ‘enter’.',2),(3,'delicious','/dɪˈlɪʃəs/','audio/1743814b25cd403196ded44232479792.mp3','Late Middle English: from Old French *delicius, from Latin deliciosus, from deliciae ‘delights’.',2),(4,'flavor','/ˈfleɪvər/','audio/3b006cb00f8c48e188d5d3862c14bbbb.mp3','Middle English savour, from Old French savour, from Latin sapere ‘to taste’',2),(5,'cuisine','/kwɪˈziːn/','audio/0ed8a6b477bd4758bd138b6423314708.mp3','French',2),(6,'ingredients','/ɪnˈɡriːdiənts/','audio/ce6faaaaa581487b8cf557770633c55a.mp3','Middle English: from Old French ingredient, from Latin ingrediens, present participle of ingredi ‘enter’.',2),(7,'restaurant','/ˈrɛstərɒnt/','audio/fe50f6f6af8e4e90957f49da72d9c85f.mp3','French, from restaurer \'to restore\'',2),(8,'oven','/ˈʌvən/','audio/6675ead3c42449bf85f86fb40c48a0fc.mp3','Middle English: from Old French, from Latin *ōvenum (from *ōvus ‘egg’)',1),(9,'chef','/ʃɛf/','audio/a5647ead8f5a4f448f87b6bd3a04d80e.mp3','From French, ultimately from Arabic',1),(10,'cooking','/ˈkʊkɪŋ/','audio/34f1a4b5255d47f08e921016c2e0967b.mp3','From Middle English *coken, from Old English *coc, from Proto-Germanic *kokōn.',2),(11,'appetizer','/ˈæpətaɪzər/','audio/9abd0337587e40a9a0cede380cfd73f1.mp3','From French *appetizer, from apétit \'appetite\'',2),(12,'astronaut','/ˈæstrənɔːt/','audio/ccd29f46b5254410a3b2caf0b1c11538.mp3','From Greek astro- (“star”) + nautēs (“sailor”)',3),(13,'rocket','/ˈrɒkɪt/','audio/4aea74cc920e4a12ae7b3f0df3b9098d.mp3','Mid 16th century (in the sense ‘spindle’): from Italian rocchetta, diminutive of rocca ‘diststaff, rock’.',3),(14,'galaxy','/ˈɡæləksi/','audio/85835